import UIKit

final class OverviewWireframe: BaseWireframe {

    // MARK: - Private properties -

    // MARK: - Module setup -

    init() {
        let moduleViewController = OverviewViewController()
        super.init(viewController: moduleViewController)

        let interactor = OverviewInteractor()
        let presenter = OverviewPresenter(view: moduleViewController, interactor: interactor, wireframe: self)
        moduleViewController.presenter = presenter
    }

